css:
:host { display: block; font-family: var(--font-interface); }
* { box-sizing: border-box; }

.vibe-widget {
    background: var(--background-secondary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 24px;
    padding: 1.5rem;
    color: var(--text-normal);
    box-shadow: var(--shadow-s);
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 1.25rem;
    position: relative;
    overflow: hidden;
}

.widget-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.widget-title {
    margin: 0;
    font-size: 0.8rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--text-muted);
}

.btn-icon {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 8px;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.2s;
}
.btn-icon:hover { background: var(--background-modifier-hover); }

.section {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

label {
    font-size: 0.65rem;
    font-weight: 800;
    color: var(--text-muted);
    letter-spacing: 0.05em;
}

.mood-grid {
    display: flex;
    justify-content: space-between;
    background: var(--background-primary);
    padding: 10px;
    border-radius: 16px;
    border: 1px solid var(--background-modifier-border);
}

.mood-item {
    font-size: 1.75rem;
    cursor: pointer;
    transition: all 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    filter: grayscale(100%);
    opacity: 0.4;
}
.mood-item:hover { transform: scale(1.2); opacity: 0.8; filter: grayscale(0%); }
.mood-item.active { filter: grayscale(0%); opacity: 1; transform: scale(1.25); }

.label-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.value-badge {
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    font-size: 0.7rem;
    font-weight: 800;
    padding: 2px 8px;
    border-radius: 10px;
}

.energy-container {
    padding: 15px 0;
    display: flex;
    align-items: center;
}

#energy-slider {
    -webkit-appearance: none;
    width: 100%;
    height: 6px;
    background: var(--background-modifier-border);
    border-radius: 3px;
    outline: none;
    cursor: pointer;
}

/* Thumb */
#energy-slider::-webkit-slider-thumb {
    -webkit-appearance: none;
    height: 18px;
    width: 18px;
    border-radius: 50%;
    background: var(--background-primary);
    border: 2px solid var(--interactive-accent);
    box-shadow: var(--shadow-s);
    transition: transform 0.1s ease, background 0.1s ease;
}

#energy-slider:active::-webkit-slider-thumb {
    transform: scale(1.2);
    background: var(--interactive-accent);
}

input[type="text"] {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    color: var(--text-normal);
    padding: 10px 14px;
    border-radius: 12px;
    font-size: 0.85rem;
    outline: none;
    transition: border-color 0.2s;
}
input[type="text"]:focus { border-color: var(--interactive-accent); }

.btn-save {
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    border: none;
    padding: 14px;
    border-radius: 16px;
    font-weight: 800;
    font-size: 0.9rem;
    cursor: pointer;
    transition: all 0.2s;
    box-shadow: 0 4px 12px rgba(var(--interactive-accent-rgb), 0.3);
}
.btn-save:hover { transform: translateY(-2px); box-shadow: 0 6px 16px rgba(var(--interactive-accent-rgb), 0.4); }

.history-view {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    animation: slideIn 0.3s ease;
}

@keyframes slideIn {
    from { transform: translateX(20px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
}

.history-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.btn-back {
    background: transparent;
    border: none;
    color: var(--interactive-accent);
    font-size: 0.7rem;
    font-weight: 700;
    cursor: pointer;
}

.history-list {
    display: flex;
    flex-direction: column;
    gap: 8px;
    max-height: 250px;
    overflow-y: auto;
    padding-right: 4px;
}

.history-item {
    background: var(--background-primary);
    border: 1px solid var(--background-modifier-border);
    border-radius: 12px;
    padding: 10px 12px;
    display: flex;
    align-items: center;
    gap: 12px;
}

.hist-mood { font-size: 1.25rem; }
.hist-content { flex: 1; display: flex; flex-direction: column; gap: 2px; }
.hist-meta { display: flex; justify-content: space-between; align-items: center; }
.hist-date { font-size: 0.6rem; color: var(--text-muted); font-weight: 600; }
.hist-energy { font-size: 0.6rem; font-weight: 800; color: #f59e0b; }
.hist-note { font-size: 0.75rem; color: var(--text-normal); font-style: italic; }

.toast {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%) translateY(100px);
    background: var(--interactive-accent);
    color: var(--text-on-accent);
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    transition: transform 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    z-index: 100;
}
.toast.show { transform: translateX(-50%) translateY(0); }

.hidden { display: none !important; }

