const { root, saveState, getState } = api;

let state = {
    currentMood: '',
    currentEnergy: 5,
    currentNote: '',
    history: []
};

function render() {
    // Update Mood Selection
    root.querySelectorAll('.mood-item').forEach(item => {
        item.classList.toggle('active', item.getAttribute('data-val') === state.currentMood);
    });

    // Update Energy Slider
    const slider = root.getElementById('energy-slider');
    const valBadge = root.getElementById('energy-val');

    slider.value = state.currentEnergy;
    valBadge.innerText = state.currentEnergy;

    // Dynamic fill effect
    const percent = ((state.currentEnergy - slider.min) / (slider.max - slider.min)) * 100;
    slider.style.background = `linear-gradient(to right, var(--interactive-accent) ${percent}%, var(--background-modifier-border) ${percent}%)`;

    // Update Note
    root.getElementById('note-input').value = state.currentNote;

    renderHistory();
}

function renderHistory() {
    const list = root.getElementById('history-list');
    list.innerHTML = '';

    if (state.history.length === 0) {
        list.innerHTML = '<div style="text-align:center; font-size:0.7rem; color:var(--text-muted); padding:20px;">No vibes logged yet.</div>';
        return;
    }

    // Show last 10 entries
    state.history.slice(-10).reverse().forEach(item => {
        const div = document.createElement('div');
        div.className = 'history-item';
        div.innerHTML = `
            <div class="hist-mood">${item.mood}</div>
            <div class="hist-content">
                <div class="hist-meta">
                    <span class="hist-date">${item.date}</span>
                    <span class="hist-energy">E: ${item.energy}/10</span>
                </div>
                ${item.note ? `<div class="hist-note">"${item.note}"</div>` : ''}
            </div>
        `;
        list.appendChild(div);
    });
}

function logVibe() {
    if (!state.currentMood) {
        showToast('Please select a mood! ğŸ˜¶');
        return;
    }

    const entry = {
        mood: state.currentMood,
        energy: state.currentEnergy,
        note: state.currentNote,
        date: new Date().toLocaleDateString(undefined, { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' })
    };

    state.history.push(entry);
    state.currentNote = ''; // Clear note after logging

    save();
    render();
    showToast('Vibe Logged! ğŸš€');
}

function toggleView(view) {
    const main = root.getElementById('main-view');
    const hist = root.getElementById('history-view');
    if (view === 'history') {
        main.classList.add('hidden');
        hist.classList.remove('hidden');
    } else {
        main.classList.remove('hidden');
        hist.classList.add('hidden');
    }
}

function showToast(msg) {
    const toast = root.getElementById('toast');
    if (toast) {
        toast.innerText = msg;
        toast.classList.add('show');
        setTimeout(() => toast.classList.remove('show'), 2000);
    }
}

function save() {
    saveState(state);
}

async function init() {
    const saved = await getState();
    if (saved) {
        // Migration check
        if (saved.history) Object.assign(state, saved);
        else if (saved.mood) { // Old format
            state.currentMood = saved.mood;
            state.currentEnergy = saved.energy || 5;
        }
    }

    // Event Listeners
    root.querySelectorAll('.mood-item').forEach(item => {
        item.onclick = () => {
            state.currentMood = item.getAttribute('data-val');
            render();
        };
    });

    const slider = root.getElementById('energy-slider');
    slider.oninput = (e) => {
        state.currentEnergy = parseInt(e.target.value);
        render();
    };

    root.getElementById('note-input').oninput = (e) => {
        state.currentNote = e.target.value;
    };

    root.getElementById('btn-save').onclick = logVibe;
    root.getElementById('btn-history-toggle').onclick = () => toggleView('history');
    root.getElementById('btn-back').onclick = () => toggleView('main');

    root.getElementById('btn-reset').onclick = () => {
        if (confirm('Clear all history?')) {
            state.history = [];
            save();
            render();
        }
    };

    render();
}

init();
