css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #0ea5e9;
    color: white;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(14, 165, 233, 0.3);
    text-align: center;
}
.widget-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem; }
.widget-title { margin: 0; font-size: 0.85rem; font-weight: 800; text-transform: uppercase; letter-spacing: 1px; }
.goal-display { font-size: 0.75rem; font-weight: 700; opacity: 0.8; }

.progress-section {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 12px;
    margin-bottom: 1.5rem;
}

.water-fill-container {
    width: 120px;
    height: 120px;
    border-radius: 50%;
    background: rgba(255,255,255,0.2);
    border: 4px solid white;
    position: relative;
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
}

.water-fill {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 0%;
    background: white;
    transition: height 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.percentage {
    position: relative;
    z-index: 1;
    font-size: 1.5rem;
    font-weight: 900;
    color: #0ea5e9;
    mix-blend-mode: difference;
}

.current-amount { font-size: 1rem; font-weight: 700; }

.quick-add {
    display: flex;
    gap: 8px;
    justify-content: center;
}

.quick-add button {
    background: white;
    color: #0ea5e9;
    border: none;
    padding: 8px 16px;
    border-radius: 12px;
    font-weight: 800;
    font-size: 0.8rem;
    cursor: pointer;
    transition: transform 0.2s;
}
.quick-add button:hover { transform: scale(1.05); }

.btn-reset { background: rgba(0,0,0,0.1) !important; color: white !important; width: 40px; }

js:
const { root, saveState, getState } = api;

let current = 0;
const goal = 2500;

async function init() {
    const saved = await getState();
    if (saved !== null) current = saved;
    render();
}

function render() {
    const percent = Math.min(Math.round((current / goal) * 100), 100);
    root.getElementById('water-fill').style.height = `${percent}%`;
    root.getElementById('percent-text').innerText = `${percent}%`;
    root.getElementById('current-val').innerText = current;
}

addWater = async (amount) => {
    current += amount;
    render();
    await saveState(current);
};

resetWater = async () => {
    if (confirm("Reset daily water intake?")) {
        current = 0;
        render();
        await saveState(current);
    }
};

init();
