css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: linear-gradient(to bottom, #4facfe 0%, #00f2fe 100%);
    color: white;
    border-radius: 24px;
    padding: 2rem;
    box-shadow: 0 15px 35px rgba(79, 172, 254, 0.3);
    text-align: center;
}

.location { font-size: 1.5rem; font-weight: 800; margin-bottom: 4px; }
.date { font-size: 0.9rem; opacity: 0.8; margin-bottom: 2rem; }

.temp-container { margin-bottom: 2.5rem; }
.temp { font-size: 5rem; font-weight: 800; line-height: 1; display: block; }
.condition { font-size: 1.2rem; font-weight: 600; text-transform: uppercase; letter-spacing: 2px; }

.forecast {
    display: flex;
    justify-content: space-between;
    background: rgba(255,255,255,0.2);
    padding: 15px;
    border-radius: 16px;
    margin-bottom: 1.5rem;
}

.day { display: flex; flex-direction: column; gap: 4px; }
.day span { font-size: 0.75rem; font-weight: 700; opacity: 0.8; }
.day b { font-size: 1rem; }

.input-area {
    display: flex;
    gap: 8px;
}

.input-area input {
    flex: 1;
    background: rgba(255,255,255,0.2);
    border: 1px solid rgba(255,255,255,0.3);
    color: white;
    border-radius: 10px;
    padding: 8px 12px;
    font-size: 0.85rem;
    outline: none;
}
.input-area input::placeholder { color: rgba(255,255,255,0.6); }

.input-area button {
    background: white;
    color: #4facfe;
    border: none;
    border-radius: 10px;
    padding: 8px 16px;
    font-weight: 700;
    cursor: pointer;
}

js:
const { root, saveState, getState } = api;

let state = {
    city: "San Francisco",
    temp: 22,
    condition: "Sunny"
};

function updateUI() {
    root.getElementById('location').innerText = state.city;
    root.getElementById('temp').innerText = `${state.temp}°`;
    root.getElementById('condition').innerText = state.condition;
    root.getElementById('date').innerText = new Date().toLocaleDateString('en-US', { weekday: 'long', day: 'numeric', month: 'short' });
}

function updateWeather() {
    const city = root.getElementById('city-input').value.trim();
    if (!city) return;

    state.city = city;
    state.temp = Math.floor(Math.random() * 15) + 10;
    const conditions = ["Sunny", "Cloudy", "Rainy", "Partly Cloudy"];
    state.condition = conditions[Math.floor(Math.random() * conditions.length)];

    updateUI();
    saveState(state);
}

async function init() {
    const saved = await getState();
    if (saved) state = saved;
    updateUI();
}

init();
