css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #ef4444;
    color: white;
    border-radius: 24px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(239, 68, 68, 0.3);
    text-align: center;
    transition: background 0.3s;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 800; text-transform: uppercase; letter-spacing: 2px; }

.timer-circle {
    width: 160px;
    height: 160px;
    border-radius: 50%;
    border: 6px solid rgba(255,255,255,0.3);
    margin: 0 auto 1.5rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}
.timer-val { font-size: 2.5rem; font-weight: 900; font-family: var(--font-monospace); }
.timer-status { font-size: 0.8rem; font-weight: 800; opacity: 0.8; }

.settings-row {
    display: flex;
    justify-content: center;
    gap: 12px;
    margin-bottom: 1.5rem;
    background: rgba(0,0,0,0.1);
    padding: 10px;
    border-radius: 12px;
}
.set-group { font-size: 0.7rem; font-weight: 700; }
.set-group label { display: block; opacity: 0.7; margin-bottom: 2px; }
.set-group input {
    width: 40px;
    background: rgba(255,255,255,0.2);
    border: none;
    color: white;
    text-align: center;
    border-radius: 4px;
    font-weight: 800;
    padding: 2px;
}

.controls { display: flex; align-items: center; justify-content: center; gap: 12px; margin-bottom: 1rem; }
.btn-main {
    background: white;
    color: #ef4444;
    border: none;
    padding: 12px 32px;
    border-radius: 12px;
    font-weight: 800;
    font-size: 1.1rem;
    cursor: pointer;
    box-shadow: 0 4px 0 #dc2626;
}
.btn-main:active { transform: translateY(2px); box-shadow: 0 2px 0 #dc2626; }
.btn-reset { background: none; border: none; color: white; font-size: 1.5rem; cursor: pointer; opacity: 0.7; }

.sets-left { font-size: 0.85rem; font-weight: 700; opacity: 0.9; }

js:
const { root, saveState, getState } = api;

let timerId = null;
let timeLeft = 0;
let currentSet = 0;
let totalSets = 8;
let isWork = true;
let workTime = 30;
let restTime = 10;

async function init() {
    const saved = await getState();
    if (saved) {
        workTime = saved.workTime || 30;
        restTime = saved.restTime || 10;
        totalSets = saved.totalSets || 8;
        root.getElementById('work-s').value = workTime;
        root.getElementById('rest-s').value = restTime;
        root.getElementById('sets-n').value = totalSets;
    }
    resetTimer();
}

function updateDisplay() {
    const m = Math.floor(timeLeft / 60);
    const s = timeLeft % 60;
    root.getElementById('timer-display').innerText = `${m < 10 ? '0' : ''}${m}:${s < 10 ? '0' : ''}${s}`;
    root.getElementById('sets-left').innerText = `Sets: ${currentSet} / ${totalSets}`;
}

toggleTimer = () => {
    if (timerId) {
        clearInterval(timerId);
        timerId = null;
        root.getElementById('start-btn').innerText = "START";
    } else {
        workTime = parseInt(root.getElementById('work-s').value);
        restTime = parseInt(root.getElementById('rest-s').value);
        totalSets = parseInt(root.getElementById('sets-n').value);
        saveState({ workTime, restTime, totalSets });

        if (timeLeft === 0) {
            timeLeft = workTime;
            currentSet = 1;
            isWork = true;
        }

        root.getElementById('start-btn').innerText = "PAUSE";
        timerId = setInterval(() => {
            timeLeft--;
            if (timeLeft < 0) {
                if (isWork) {
                    isWork = false;
                    timeLeft = restTime;
                    root.querySelector('.widget-card').style.background = '#3b82f6';
                    root.getElementById('timer-status').innerText = "REST";
                } else {
                    currentSet++;
                    if (currentSet > totalSets) {
                        clearInterval(timerId);
                        timerId = null;
                        resetTimer();
                        alert("Workout Complete! ðŸ†");
                        return;
                    }
                    isWork = true;
                    timeLeft = workTime;
                    root.querySelector('.widget-card').style.background = '#ef4444';
                    root.getElementById('timer-status').innerText = "WORK";
                }
            }
            updateDisplay();
        }, 1000);
    }
};

resetTimer = () => {
    clearInterval(timerId);
    timerId = null;
    timeLeft = parseInt(root.getElementById('work-s').value);
    currentSet = 0;
    isWork = true;
    root.getElementById('start-btn').innerText = "START";
    root.getElementById('timer-status').innerText = "READY";
    root.querySelector('.widget-card').style.background = '#ef4444';
    updateDisplay();
};

init();
