css:
:host { display: block; margin: 1.5rem 0; font-family: var(--font-interface); }
.widget-card {
    background: #18181b;
    color: #f4f4f5;
    border-radius: 16px;
    padding: 1.5rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.2);
    border: 1px solid #27272a;
}
.widget-title { margin: 0 0 1.5rem 0; font-size: 0.9rem; font-weight: 700; color: #a1a1aa; text-transform: uppercase; letter-spacing: 1px; text-align: center; }

.clock-list { display: grid; gap: 12px; }
.clock-item {
    background: #27272a;
    border-radius: 12px;
    padding: 12px 16px;
    display: flex;
    flex-direction: column;
    align-items: center;
    border: 1px solid transparent;
    transition: all 0.3s;
}
.clock-item.active {
    background: #3f3f46;
    border-color: #0ea5e9;
    transform: scale(1.05);
}

.city { font-size: 0.75rem; font-weight: 700; color: #a1a1aa; text-transform: uppercase; margin-bottom: 4px; }
.time { font-size: 1.75rem; font-weight: 800; font-family: var(--font-monospace); color: #f4f4f5; line-height: 1; }
.date { font-size: 0.7rem; color: #71717a; margin-top: 4px; }

js:
const { root } = api;

function updateClocks() {
    updateCity('ny-time', 'ny-date', 'America/New_York');
    updateCity('ldn-time', 'ldn-date', 'Europe/London');
    updateCity('tky-time', 'tky-date', 'Asia/Tokyo');
}

function updateCity(timeId, dateId, timezone) {
    const now = new Date();
    const timeStr = now.toLocaleTimeString('en-US', { 
        timeZone: timezone, 
        hour12: false, 
        hour: '2-digit', 
        minute: '2-digit', 
        second: '2-digit' 
    });
    const dateStr = now.toLocaleDateString('en-US', { 
        timeZone: timezone, 
        month: 'short', 
        day: 'numeric', 
        year: 'numeric' 
    });

    root.getElementById(timeId).innerText = timeStr;
    root.getElementById(dateId).innerText = dateStr;
}

setInterval(updateClocks, 1000);
updateClocks();
